import React, { useState } from 'react';
import './FilmCard.css';
import ReviewForm from './ReviewForm'
import Star from '../resources/starGray'
import ReviewsPeoples from './ReviewsPeoples'



const FilmCard = ({ dataFilms }) => {


    const [stateAllfimls,setStateallfilms] = useState(dataFilms)
    // const [stateAllReviews, setStateallreviews] = useState(allReviews)

    function changerStatedataFilms(){
        console.log(stateAllfimls)
        setStateallfilms([
            ...stateAllfimls,
            {
                id: 1,
                title: "Бэтмен",
                posterLink: "https://avatars.mds.yandex.net/get-kinopoisk-image/6201401/4d924203-de93-4f05-9564-94ff08678516/3840x",
                date: 2022,
                rating: 7.9,
                review: '',
                reviews: [
                  {
                      name_all_reviews: '777788',
                      data_all_reviews: '666 dec',
                      otziv_all_reviews: 'polnoe efefjefeklle'
                    },
                  {
                  name_all_reviews: 'govnoizjopi',
                  data_all_reviews: '666 dec',
                  otziv_all_reviews: 'polnoe efefjefeklle'
                }
              ],
                myRating: '',
                description: "После двух лет поисков правосудия на улицах Готэма Бэтмен становится для горожан олицетворением беспощадного возмездия. Когда в городе происходит серия жестоких нападений на высокопоставленных чиновников, улики приводят Брюса Уэйна в самые тёмные закоулки преступного мира, где он встречает Женщину-Кошку, Пингвина, Кармайна Фальконе и Загадочника. Теперь под прицелом оказывается сам Бэтмен, которому предстоит отличить друга от врага и восстановить справедливость во имя Готэма."
            }
        ])
    }


    // function changerStateReviews() {
    //     // console.log(stateAllReviews)
    //     setStateallreviews([
    //         ...stateAllReviews,
    //         {
    //             // id: nextId++,
    //             name_all_reviews: 'gena',
    //             data_all_reviews: '20 dec',
    //             zvezdi_all_reviews: '',
    //             otziv_all_reviews:'govno',

    //         }
    //     ])
    //   }

    // const jopa = () => {
    //     stateAllReviews.ob={
    //         name_all_reviews:'5',
    //         data_all_reviews:'7',
    //         zvezdi_all_reviews:'',
    //         otziv_all_reviews:'govno',
    //     })
    //     console.log(allReviews)
    // }

    // const { title, posterLink, description } = dataFilms
    let newPlates

    if (dataFilms.length) {
        newPlates = dataFilms.map(function (item) {
            const  jopajopa = item.reviews
            console.log(jopajopa)
            return (
                <div className='oneFilmBig'>
                    <div className="oneFilm" key={item.id}>
                        <div className="list_container" >
                            <img className="films_posterLink" src={item.posterLink} />
                            {/* <p className="films_date">Date:{item.date}</p>
                        <p className="films_rating">Rating:{item.rating}</p> */}
                        </div>
                        <div className="desc_container">
                            <p className="films_title">{item.title}:</p>
                            <div className='rate_review'>
                                <div className='average_rate'>
                                    <svg className='star_IMG'>{Star}</svg>
                                    <svg className='star_IMG'>{Star}</svg>
                                    <svg className='star_IMG'>{Star}</svg>
                                    <svg className='star_IMG'>{Star}</svg>
                                    <svg className='star_IMG'>{Star}</svg>
                                </div>
                                <div className='summa_review' onClick={changerStatedataFilms}> отзывы (число)</div>
                            </div>


                            <div className="films_description">{item.description}</div>

                        </div>

                        {/* <div className="review_contetn"> */}
                        {/* <button className="films_feedback_click">review</button> */}
                        {/* <div className="films_reviews">films_review:{item.review}</div>  */}
                        {/* </div> */}
                    </div>
                    <ReviewForm props2 = {jopajopa}/>
                    {/* <ReviewsPeoples stateAllReviews ={stateAllReviews}/>  */}
                    <ReviewsPeoples props2 = {jopajopa}/> 


                </div>

            )
        })
    } else {
        newPlates = <p>Добавьте фильм</p>
    }
    return (
        <div className="films">
            {newPlates}
        </div>


    )
}

export default FilmCard;

