        <form className='add'>
            <input
                id='author_name'
                type='text'
                className='add__author'
                placeholder='Ваше имя'
                value={author_name}
                onChange={this.event_handler}
            />
            <p />
            <textarea
                id='text_news'
                className='add__text'
                placeholder='Текст новости'
                value={text_news}
                onChange={this.event_handler}
            ></textarea>
            <p />
            <label className='add__checkrule'>
                <input type='checkbox'
                    checked={check_box}
                    onChange={this.value_add__checkrule}
                /> Я согласен с правилами
            </label>
            <p />
            <button
                disabled={!this.validate()}
                className='add__btn'
                onClick={this.onBtnClickHandler}>
                Показать alert
            </button>
        </form>
    )
}



export default ReviewForm;
