import React, { useStateAllreviews } from 'react';
import './FilmCard.css';
import ReviewForm from './ReviewForm'
import Star from '../resources/starGray'
import ReviewsPeoples from './ReviewsPeoples'



const FilmCard = ({ dataFilms }) => {

    const allReviews = [
        {
            // id: '',
            name_all_reviews: 'valera',
            data_all_reviews: '18 dec',
            zvezdi_all_reviews: '',
            otziv_all_reviews: 'govno'
        },
        {
            // id: '',
            name_all_reviews: 'jenak',
            data_all_reviews: '19 dec',
            zvezdi_all_reviews: '',
            otziv_all_reviews: 'govno 2'
        },
        {
            // id: '',
            name_all_reviews: 'gena',
            data_all_reviews: '20 dec',
            zvezdi_all_reviews: '',
            otziv_all_reviews: 'govno 3'
        }
    ]
    const [feewer] = useStateAllreviews(allReviews)

    const { title, posterLink, description } = dataFilms
    let newPlates
    if (dataFilms.length) {
        newPlates = dataFilms.map(function (item) {
            return (
                <div className='oneFilmBig'>
                    <div className="oneFilm" key={item.id}>
                        <div className="list_container" >
                            <img className="films_posterLink" src={item.posterLink} />
                            {/* <p className="films_date">Date:{item.date}</p>
                        <p className="films_rating">Rating:{item.rating}</p> */}
                        </div>
                        <div className="desc_container">
                            <p className="films_title">{item.title}:</p>
                            <div className='rate_review'>
                                <div className='average_rate'>
                                    <svg className='star_IMG'>{Star}</svg>
                                    <svg className='star_IMG'>{Star}</svg>
                                    <svg className='star_IMG'>{Star}</svg>
                                    <svg className='star_IMG'>{Star}</svg>
                                    <svg className='star_IMG'>{Star}</svg>
                                </div>
                                <div className='summa_review'> отзывы (число)</div>
                            </div>


                            <div className="films_description">{item.description}</div>
                        </div>

                        {/* <div className="review_contetn"> */}
                        {/* <button className="films_feedback_click">review</button> */}
                        {/* <div className="films_reviews">films_review:{item.review}</div>  */}
                        {/* </div> */}

                    </div>
                    <ReviewForm />
                    <ReviewsPeoples rrrr ={feewer}/>


                </div>

            )
        })
    } else {
        newPlates = <p>Добавьте фильм</p>
    }
    return (
        <div className="films">
            {newPlates}
        </div>


    )
}

export default FilmCard;
