  }
]

const getFilmCards = () => {
  return favoritFilms.map((item,index) => {
    return <FilmCard filmData={item} key={index} />
  });
}

const App = () => {
  return (
    <React.Fragment>
      <div className="films">
        {favoritFilms.length ? getFilmCards() : <p>Нет фильмов</p>}
      </div>
    </React.Fragment>
  )
}

export default App;
