const ReviewForm = ({ props3 }) => {
    const [stateReviwPeoples,setStateallfilms] = useState(props3)


    function testAddreviews(){
        console.log(stateReviwPeoples)
        setStateallfilms([
            ...stateReviwPeoples,
            {
                id: 1,
                title: "Бэтмен",
                posterLink: "https://avatars.mds.yandex.net/get-kinopoisk-image/6201401/4d924203-de93-4f05-9564-94ff08678516/3840x",
                date: 2022,
                rating: 7.9,
                review: '',
                reviews: [
                  {
                      name_all_reviews: '777788',
                      data_all_reviews: '666 dec',
                      otziv_all_reviews: 'polnoe efefjefeklle'
                    },
                  {
                  name_all_reviews: 'govnoizjopi',
                  data_all_reviews: '666 dec',
                  otziv_all_reviews: 'polnoe efefjefeklle'
                }
              ],
                myRating: '',
                description: "После двух лет поисков правосудия на улицах Готэма Бэтмен становится для горожан олицетворением беспощадного возмездия. Когда в городе происходит серия жестоких нападений на высокопоставленных чиновников, улики приводят Брюса Уэйна в самые тёмные закоулки преступного мира, где он встречает Женщину-Кошку, Пингвина, Кармайна Фальконе и Загадочника. Теперь под прицелом оказывается сам Бэтмен, которому предстоит отличить друга от врага и восстановить справедливость во имя Готэма."
            }
        ])
    }
    const testtest3 = () =>{
        console.log('fwfwf')
    }


    return (

        <div className='ReviewForm'>
            <div id='headerReviewForm'>
                <input
                    id='nameReview'
                    type='text'
                    className='name_Review'
                    placeholder='Ваше имя'
                ></input>

                <Stars />

            </div>
            <input
                id='textReview'
                className='text_Review'
                placeholder='Отзыв о фильме'
            ></input>
            <button className='add__btn' onClick={testAddreviews}> Оставить отзыв </button>


        </div>

    )
}



